api/admin/login.ts.content:
import type { VercelRequest, VercelResponse } from '@vercel/node';
import crypto from 'crypto';

function generateToken(secret: string): string {
  const timestamp = Date.now().toString();
  const randomBytes = crypto.randomBytes(32).toString("hex");
  const payload = `${timestamp}:${randomBytes}`;
  const signature = crypto.createHmac("sha256", secret).update(payload).digest("hex");
  return Buffer.from(`${payload}:${signature}`).toString("base64");
}

export default async function handler(req: VercelRequest, res: VercelResponse) {
  if (req.method !== 'POST') {
    return res.status(405).json({ error: 'Method not allowed' });
  }

  try {
    const { password } = req.body;
    const adminPassword = process.env.ADMIN_PASSWORD;

    if (!adminPassword) {
      console.error("ADMIN_PASSWORD not configured");
      return res.status(500).json({ error: "Admin authentication not configured" });
    }

    if (password !== adminPassword) {
      return res.status(401).json({ error: "Invalid password" });
    }

    const token = generateToken(adminPassword);
    return res.json({ token });
  } catch (error) {
    console.error('Error in admin login:', error);
    return res.status(500).json({ error: 'Login failed' });
  }
}

api/admin/stats.ts.content:
import type { VercelRequest, VercelResponse } from '@vercel/node';
import crypto from 'crypto';
import { getClickStats, getAllPilotSignups } from '../_db';

const ADMIN_TOKEN_EXPIRY = 24 * 60 * 60 * 1000; // 24 hours

function validateToken(token: string): boolean {
  const adminPassword = process.env.ADMIN_PASSWORD;
  if (!adminPassword) return false;

  try {
    const decoded = Buffer.from(token, "base64").toString("utf-8");
    const parts = decoded.split(":");
    if (parts.length !== 3) return false;
    const [timestampStr, randomBytes, signature] = parts;
    const timestamp = parseInt(timestampStr, 10);
    if (isNaN(timestamp)) return false;
    if (Date.now() - timestamp >= ADMIN_TOKEN_EXPIRY) return false;
    const payload = `${timestampStr}:${randomBytes}`;
    const expectedSignature = crypto.createHmac("sha256", adminPassword).update(payload).digest("hex");
    return crypto.timingSafeEqual(Buffer.from(signature), Buffer.from(expectedSignature));
  } catch {
    return false;
  }
}

export default async function handler(req: VercelRequest, res: VercelResponse) {
  if (req.method !== 'GET') {
    return res.status(405).json({ error: 'Method not allowed' });
  }

  try {
    const authHeader = req.headers.authorization;
    if (!authHeader || !authHeader.startsWith("Bearer ")) {
      return res.status(401).json({ error: "No token provided" });
    }
    const token = authHeader.substring(7);
    if (!validateToken(token)) {
      return res.status(401).json({ error: "Invalid or expired token" });
    }

    const stats = await getClickStats();
    const signups = await getAllPilotSignups();

    return res.json({
      ...stats,
      signupCount: signups.length,
      recentSignups: signups.slice(0, 10)
    });
  } catch (error) {
    console.error('Error fetching stats:', error);
    return res.status(500).json({ error: 'Failed to fetch stats' });
  }
}

netlify/functions/stores.ts.content:
import type { Handler, HandlerEvent, HandlerContext } from "@netlify/functions";
import { getAllStores, getStoreById } from "../../api/_db";

export const handler: Handler = async (event: HandlerEvent, context: HandlerContext) => {
  if (event.httpMethod !== 'GET') {
    return { statusCode: 405, body: JSON.stringify({ error: 'Method not allowed' }) };
  }

  try {
    const params = event.queryStringParameters || {};
    const path = event.path
      .replace('/.netlify/functions/stores', '')
      .replace('/api/stores', '');

    // Handle store ID from path
    const pathId = path.replace('/', '').trim();
    if (pathId && !isNaN(parseInt(pathId))) {
      const store = await getStoreById(parseInt(pathId));
      if (!store) {
        return { statusCode: 404, body: JSON.stringify({ error: 'Store not found' }) };
      }
      return { 
        statusCode: 200, 
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(store) 
      };
    }

    // Handle ID in query params
    if (params.id) {
      const store = await getStoreById(parseInt(params.id));
      if (!store) {
        return { statusCode: 404, body: JSON.stringify({ error: 'Store not found' }) };
      }
      return { 
        statusCode: 200, 
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(store) 
      };
    }

    // Return all stores
    const stores = await getAllStores();
    return { 
      statusCode: 200, 
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(stores) 
    };
  } catch (error) {
    console.error('Error fetching stores:', error);
    return { statusCode: 500, body: JSON.stringify({ error: 'Failed to fetch stores' }) };
  }
};

